mapper.select:
select PROMOTION_NO as PromotionNo, PROMOTION_TITLE as PromotionTitle, PROMOTION_CONTENT as PromotionContent, PROMOTION_REGDATE as PromotionRegdate, FARM_NO as FarmNo, FARM_NAME as FarmName, PROMOTION_COUNT as PromotionCount, PROMOTION_END_YN as PromotionEnd, PROMOTIONPHOTO_NO as PromotionPhotoNo, ADVERTISE_YN as Advertise, PROMOTIONPHOTO_FILEPATH as PromotionFilePath, PROMOTIONPHOTO_ORIGINAL as promotionPhotoOriginal from (select row_number() over(order by PROMOTION_NO desc)as num, PROMOTION.* ,FARM_NAME, PROMOTIONPHOTO_FILEPATH, PROMOTIONPHOTO_ORIGINAL from PROMOTION left join FARM on (PROMOTION.FARM_NO=FARM.FARM_NO) left Join PROMOTIONPHOTO on (PROMOTION.PROMOTIONPHOTO_NO=PROMOTIONPHOTO.PROMOTIONPHOTO_NO) where PROMOTION_END_YN='N'
and PROMOTION_TITLE like '%'||#{keyword}||'%'
and PROMOTION_CONTENT like '%'||#{keyword}||'%'
and (PROMOTION_TITLE like '%'||#{keyword}||'%' or PROMOTION_CONTENT like '%'||#{keyword}||'%')
) where num between #{start} and #{end}

mapper.update:
update PROMOTION set
	PROMOTION_TITLE = #{promotionTitle},
	PROMOTION_CONTENT = #{promotionContent},
	PROMOTIONPHOTO_NO = #{promotionPhotoNo}
where PROMOTION_NO = #{promotionNo}

 	update	PROMOTION set
		PROMOTION_END_YN = 'Y'
 	where PROMOTION_NO = #{promotionNo}
